-- title:  Time Galleon Boniface
-- author: Pete Rielly
-- desc:   Collect Cosmic Gems at the end of time and space.
-- script: lua

t=0
x=120
y=68
step=math.pi/90
sin=math.sin
cos=math.cos
rnd=math.random
st=t
lt=0
size=30
state="title"

scrn={0,0,240,136}

sunx=120
suny=75
sunr=20

startx=120
starty=20
sacc=0.2
friction=0.92
ramspd=2
g=.06


blocks={}
ship={}
particles={}
stars={}
goats={}

function newGoat(x,y)
    local ang=rnd()*math.pi*2
    local g={
        x=x,
        y=y,
        vx=cos(ang)*.7,
        vy=sin(ang)*.7,
        trail={},
        tl=16,
        ti=1,
        draw=function(a)
            local flip=1
            if a.vx>0 then flip =0 else flip=1 end
            for _,t in ipairs(a.trail) do
                spr(70,t.x,t.y,0,1,flip)
            end
            spr(65,a.x,a.y,0,1,flip)
        end,
        update=function(a)
            a.x=a.x+a.vx
            a.y=a.y+a.vy
            if a.x <1 or a.x > 239 then a.vx=a.vx*-1 end
            if a.y <1 or a.y > 135 then a.vy=a.vy*-1 end
            a.trail[a.ti]=nil
            a.trail[a.ti]={}
            a.trail[a.ti].x=a.x
            a.trail[a.ti].y=a.y
            a.ti=a.ti+1
            if a.ti >= a.tl then a.ti=1 end
        end
    }
    return g
end


function newStar(a,c,r)
    local n ={
        x=0,
        y=0,
        a=a,
        c=c,
        r=r,
        ci=1,
        s=1+rnd(),
        draw=function(p)
            p.ci=p.ci+1
            if p.ci > #p.c then p.ci=1 end
            rect(p.x,p.y,p.r/125*5,p.r/125*5,p.c[p.ci])
        end,
        update=function(p)
            p.r=p.r-0.5
            if p.r < sunr then p.r=125 end
            p.a=p.a+math.pi/360
            p.x=sunx+math.cos(p.a)*p.r
            p.y=suny-math.sin(p.a)*p.r
        end
    }
    return n

end

for i=1,200 do
    local s=newStar(
                    2*rnd()*math.pi,
                    {rnd(1,15),rnd(1,15),rnd(1,15)},
                    rnd(125)
                )
    stars[i]=s
end

function starField()
    for _,s in ipairs(stars) do
        s:update()
        s:draw()
    end

end

music(0,0,0,true,false,100,22)

function end_tic()
    printc("Game Over",120,50,5,2)
    map(0,34,7,3,0,0,11)
    print(lt,8,10,2)
    if btnp(4) or btnp(5) then
		state="title"
    end
end

function win_tic()
    starField()
    rect(10,20,220,110,0)
    printc("Mission Complete",120,25,5,2)
    printc("On defeat the Hyper-goats confess",120,40,5,1)
    printc("their most guarded secret.",120,50,5,1)
    printc("The Cosmic Gems that collect",120,60,5,1)
    printc("at the event horizon",120,70,5,1)
    printc("are the distilled essence of",120,80,5,1)
    printc("the sorrow of all creatures.",120,90,5,1)
    printc("Drawn to the Singularity",120,100,5,1)
    printc("to be held, in crystal,",120,110,5,1)
    printc("forever.",120,120,5,1)
    map(0,34,7,3,0,0,11)
    print(lt,8,10,2)
    if btnp(4) or btnp(5) then
		state="title"
    end
end

function newParticle(x,y,a,c,l)
    local n ={
        x=x,
        y=y,
        a=a,
        c=c,
        l=l,
        ci=1,
        s=1+rnd(),
        draw=function(p)
            p.ci=p.ci+1
            if p.ci > #p.c then p.ci=1 end
            rect(p.x,p.y,2,2,p.c[p.ci])
        end,
        update=function(p)
            p.l=p.l-1
            p.x=p.x+math.cos(p.a)*p.s
            p.y=p.y-math.sin(p.a)*p.s
        end
    }
    return n

end

function calc_angle(x1,y1,x2,y2)
	return math.atan2(y1-y2,x2-x1)
end

function AABB(x1,y1,w1,h1,x2,y2,w2,h2)

        if x1 <= x2+w2 and x1+w2 >= x2 then
            if y1 <= y2+h2 and y1+h1 >= y2 then
                return true
            end
        end
    end

function newBlock(x,y)
    local b={
        x=x,
        y=y,
        state=1,
        draw=function(b)
            if b.state==1 then
                spr(1,b.x,b.y,0)
            end
        end,
        drawShadow=function(b)
            if b.state==1 then
                circ(cos(t/5+b.x)+b.x+4,cos(t/5+b.y)+b.y+4,rnd()+9+sin((t+math.random(1,3))/6)*2,8)
            end
        end
    }
    return b
end

function newShip(x,y)
    local s={
        x=x,
        y=y,
        vx=0,
        vy=0,
        dir=0,
        speed=0,
        alive=true,
        dt=0,
        trail={},
        tl=8,
        ti=1,
        itime=0,
        draw=function(a)
            if a.alive then
                for _,t in ipairs(a.trail) do
                    if a.speed >= ramspd then
                        spr(69,t.x,t.y,0)
                    else
                        spr(67,t.x,t.y,0)
                    end
                end
                if a.speed >= ramspd then
                    spr(68,a.x,a.y,0,1,a.dir)
                else
                    spr(64,a.x,a.y,0,1,a.dir)
                end
            end
        end,
        update=function(a)
            a.x=a.x+a.vx
            a.y=a.y+a.vy

            if a.x<scrn[1] then a.x=scrn[1] end
            if a.x>scrn[3]-8 then a.x=scrn[3]-8 end
            if a.y<scrn[2] then a.y=scrn[2] end
            if a.y>scrn[4]-8 then a.y=scrn[4]-8 end

            a.vx=a.vx*friction
            a.vy=a.vy*friction
            --gravity
            local ang = calc_angle(a.x,a.y,sunx,suny)
            a.vx=a.vx+math.cos(ang)*g
            a.vy=a.vy-math.sin(ang)*g
            --clamp
            if math.abs(a.vx)<0.01 then a.vx=0 end
            if math.abs(a.vy)<0.01 then a.vy=0 end
            a.speed=math.abs(a.vx)^2+math.abs(a.vy)^2
            a.speed=a.speed^0.5
            --build trail

            a.trail[a.ti]=nil
            a.trail[a.ti]={}
            a.trail[a.ti].x=a.x
            a.trail[a.ti].y=a.y
            a.ti=a.ti+1
            if a.ti >= a.tl then a.ti=1 end

            if not a.alive then
                if a.itime==0 then
                    state="end"
                    lt=t//10
                    music(0,0,0,true,false,100,22)
                end
                a.itime=a.itime-1
                
            end

        end
    }
    return s
end

function printc(s,x,y,c,sc)
	if sc==nil then sc=1 end
	local w=print(s,0,-30,c,true,sc)
	print(s,x-(w/2),y,c or 15,true,sc)
end

function mapToBlock(mx,my)
    for i=0,29 do
        for j=0,16 do
            if mget(mx+i,my+j) == 1 then
                local b = newBlock(i*8,j*8)
                table.insert(blocks,b)
            end
        end
    end
end

function init()
    for i,_ in ipairs(blocks) do
        blocks[i]=nil
    end
    for i,_ in ipairs(particles) do
        particles[i]=nil
    end
    for i,_ in ipairs(goats) do
        goats[i]=nil
    end
    for i=1,3 do
        goats[i]=newGoat(rnd(1,230),rnd(50,120))
    end
    mapToBlock(0,0)
    ship=nil
    ship=newShip(startx, starty)
    t=0
end

function title_tic()
    cls(0)
    starField()
    printc("Time Galleon",120,60,5,2)
    printc("Boniface",120,72,5,2)
    printc("or",120,84,5,1)
    printc("Hark the Wisdom of the Hyper-goat",120,92,5,1)

    if btnp(4) or btnp(5) then
		state="play"
        init()
        music()
    end
end

function play_tic()

	if btn(0) then 
        if true then
            ship.vy=ship.vy-sacc  
        end
    end
    if btn(1) then 
        if true then 
            ship.vy=ship.vy+sacc  
        end
    end
    if btn(2) then 
        if true then
            ship.dir=1
            ship.vx=ship.vx-sacc  
        end
    end
    if btn(3) then 
        if true then
            ship.dir=0
            ship.vx=ship.vx+sacc
        end
    end

    -- updates
    ship:update()

    cls(0)
    starField()

    circ(sunx,suny,sunr+(sin(t/2)*1.2),1)
    circ(sunx,suny,sunr*.75+(sin(3+t/2)*1.2),2)
    circ(sunx,suny,sunr*.3+(sin(1+t/2)*1.2),3)

    for _,b in ipairs(blocks) do
        b:drawShadow()
    end
    for _,b in ipairs(blocks) do
        b:draw()
        if ship.alive and b.state ~=0 and AABB(ship.x,ship.y,8,8,b.x,b.y,8,8) then
            if ship.speed >= ramspd then
                if b.state ~= 0 then
                    for i=0,20 do
                        table.insert(particles,newParticle(ship.x+4,ship.y+4,i*2*math.pi/20,{4,5,6,7},60))
                    end
                end
                b.state=0
                sfx(1,"B-4",50,2,15,4)
                
            end
        end
    end
    for _,p in ipairs(particles) do
        p:update()
        p:draw()
    end
    for i,p in ipairs(particles) do
        if p.l<=0 then
            table.remove(particles,i)
        end
    end

    local shipsun = math.sqrt(math.abs((ship.x+4-sunx)^2) + math.abs((ship.y+4-suny)^2))
    if shipsun < sunr and ship.alive then
        ship.alive=false
        ship.itime=60
        sfx(2,"C-1",50,3,15,4)
        for i=0,100 do
            table.insert(particles,newParticle(ship.x+4,ship.y+4,rnd()*2*math.pi,{10,11,4,2,12},60))
        end
    end

    for _,g in ipairs(goats) do
        g:update()
        g:draw()
        if ship.alive then
            if AABB(g.x,g.y,8,8,ship.x+1,ship.y+1,6,6) then
                ship.alive=false
                ship.itime=60
                sfx(2,"C-1",50,3,15,4)
                for i=0,100 do
                    table.insert(particles,newParticle(ship.x+4,ship.y+4,rnd()*2*math.pi,{10,11,4,2,12},60))
                end
            end
        end
    end

    local win=true
    for _,b in ipairs(blocks) do
        if b.state ~=0 then win=false end
    end
    if win then 
        state="win" 
        lt=t//10
    end

    ship:draw()

    if ship.x > 7*8 or ship.y >3*8 then
        map(0,34,7,3,0,0,11)
        print(t//10,8,10,5)
    end
    if state=="win" then cls(0) end
 
end

function TIC()
	t=t+1
    if state=="title" then
		title_tic()
	elseif state=="play" then
		play_tic()
	elseif state=="end" then
		end_tic()
    elseif state=="win" then
        win_tic()
    end

end

